/*
 * Checks if the current user is enrolled in a given section
 */
function is_enrolled_in_course($courseid) {
	$context = get_context_instance(CONTEXT_COURSE, $courseid, MUST_EXIST);
	return $enrolled = is_enrolled($context, $USER->id, '', true);
}

/*
 * Limit description length
 */
function cut_description($description) {
	if ( strlen($description) > 200 ) {
		return substr($description,0,200) . '...';
	} else {
		return $description;
	}
}
</pre>

