=== Grab Enrolled Programs (Categories with Subcategories) ===
<pre>
$enrolledBigCats = 0;
foreach ($bigCats as $cat) {
	$catid = $cat->id;
	$$catid = false;
	$subCats = $DB->get_records('course_categories', array('parent'=>$cat->id), null, 'id,name,idnumber,parent,sortorder,coursecount,visible,depth,path');
	//check for courses in each subcategory
	foreach ($subCats as $subCat) {
		$courses = $DB->get_records('course', array('category'=>$subCat->id), null, 'id,category,visible');
		foreach ($courses as $course) {
			$context = get_context_instance(CONTEXT_COURSE, $course->id, MUST_EXIST);
			if ( (is_enrolled($context, $USER->id, '', true) && $course->visible) || is_siteadmin()) {
				$enrolledBigCats++;
				$$catid = true;
				break 2;
			}
		}
	}
	//check for courses in the top category itself
	$courses = $DB->get_records('course', array('category'=>$cat->id), null, 'id,category,visible');
	foreach ($courses as $course) {
		$context = get_context_instance(CONTEXT_COURSE, $course->id, MUST_EXIST);
		if ( (is_enrolled($context, $USER->id, '', true) && $course->visible) || is_siteadmin()) {
			$enrolledBigCats++;
			$$catid = true;
			break 1;
		}
	}
}
